# 获取北京时间
def get_beijing_time():
    if ZoneInfo:
        # 使用Python 3.9+的标准库ZoneInfo
        target_timezone = ZoneInfo('Asia/Shanghai')
        return datetime.now().astimezone(target_timezone)
    else:
        # 兼容旧版本Python，手动调整时区
        import time
        # 获取当前时间戳
        timestamp = time.time()
        # 北京时间比UTC+8，手动加上8小时
        return datetime.fromtimestamp(timestamp + 8 * 3600)


# 格式化时间
def format_now():
    return get_beijing_time().strftime("%Y-%m-%d %H:%M:%S")


