# 虚拟ip地址
def fake_ip():
    # 随便找的国内IP段：223.64.0.0 - 223.117.255.255
    return f"36.161.{random.randint(0, 255)}.{random.randint(0, 255)}"



# 账号脱敏
def desensitize_user_name(user):
    if len(user) <= 8:
        ln = max(math.floor(len(user) / 3), 1)
        return f'{user[:ln]}***{user[-ln:]}'
    return f'{user[:3]}****{user[-4:]}'


# 获取时间戳
def get_time():
    current_time = get_beijing_time()
    return "%.0f" % (current_time.timestamp() * 1000)


# 获取登录code
def get_access_token(location):
    code_pattern = re.compile("(?<=access=).*?(?=&)")
    result = code_pattern.findall(location)
    if result is None or len(result) == 0:
        return None
    return result[0]





class MiMotionRunner:
    def __init__(self, _user, _passwd):
        user = str(_user)
        password = str(_passwd)
        self.invalid = False
        self.log_str = ""
        if user == '' or password == '':
            self.error = "用户名或密码填写有误！"
            self.invalid = True
            pass
        self.password = password
        if ("+86" in user) or "@" in user:
            user = user
        else:
            user = "+86" + user
        if "+86" in user:
            self.is_phone = True
        else:
            self.is_phone = False
        self.user = user
        self.fake_ip_addr = fake_ip()
        self.log_str += f"创建虚拟ip地址：{self.fake_ip_addr}\n"

    # 登录
    def login(self):

        url1 = "https://api-user.huami.com/registrations/" + self.user + "/tokens"
        login_headers = {
            "Content-Type": "application/x-www-form-urlencoded;charset=UTF-8",
            "User-Agent": "Mozilla/5.0 (iPhone; CPU iPhone OS 14_7_1 like Mac OS X) AppleWebKit/605.1.15 (KHTML, like Gecko) Version/14.1.2",
            "app_name": "com.xiaomi.hm.health",
            "X-Forwarded-For": self.fake_ip_addr
        }
        data1 = {
            "client_id": "HuaMi",
            "password": f"{self.password}",
            "redirect_uri": "https://s3-us-west-2.amazonaws.com/hm-registration/successsignin.html",
            "token": "access"
        }
        r1 = requests.post(url1, data=data1, headers=login_headers, allow_redirects=False)
        if r1.status_code != 303:
            self.log_str += "登录异常，status: %d\n" % r1.status_code
            return 0, 0
        location = r1.headers["Location"]
        try:
            code = get_access_token(location)
            if code is None:
                self.log_str += "获取accessToken失败\n"
                return 0, 0
        except:
            self.log_str += f"获取accessToken异常:{traceback.format_exc()}\n"
            return 0, 0
        # print("access_code获取成功！")
        # print(code)

        url2 = "https://account.huami.com/v2/client/login"
        if self.is_phone:
            data2 = {
                "app_name": "com.xiaomi.hm.health",
                "app_version": "4.6.0",
                "code": f"{code}",
                "country_code": "CN",
                "device_id": "2C8B4939-0CCD-4E94-8CBA-CB8EA6E613A1",
                "device_model": "phone",
                "grant_type": "access_token",
                "third_name": "huami_phone",
            }
        else:
            data2 = {
                "allow_registration=": "false",
                "app_name": "com.xiaomi.hm.health",
                "app_version": "6.3.5",
                "code": f"{code}",
                "country_code": "CN",
                "device_id": "2C8B4939-0CCD-4E94-8CBA-CB8EA6E613A1",
                "device_model": "phone",
                "dn": "api-user.huami.com%2Capi-mifit.huami.com%2Capp-analytics.huami.com",
                "grant_type": "access_token",
                "lang": "zh_CN",
                "os_version": "1.5.0",
                "source": "com.xiaomi.hm.health",
                "third_name": "email",
            }
        r2 = requests.post(url2, data=data2, headers=login_headers).json()
        login_token = r2["token_info"]["login_token"]
        # print("login_token获取成功！")
        # print(login_token)
        userid = r2["token_info"]["user_id"]
        # print("userid获取成功！")
        # print(userid)

        return login_token, userid

    # 获取app_token
    def get_app_token(self, login_token):
        url = f"https://account-cn.huami.com/v1/client/app_tokens?app_name=com.xiaomi.hm.health&dn=api-user.huami.com%2Capi-mifit.huami.com%2Capp-analytics.huami.com&login_token={login_token}"
        headers = {'User-Agent': 'MiFit/5.3.0 (iPhone; iOS 14.7.1; Scale/3.00)', 'X-Forwarded-For': self.fake_ip_addr}
        response = requests.get(url, headers=headers).json()
        app_token = response['token_info']['app_token']
        # print("app_token获取成功！")
        # print(app_token)
        return app_token

