def run_single_account(total, idx, user_mi, passwd_mi):
    idx_info = ""
    if idx is not None:
        idx_info = f"[{idx+1}/{total}]"
    log_str = f"[{format_now()}]\n{idx_info}账号：{desensitize_user_name(user_mi)}"
    try:
        runner = MiMotionRunner(user_mi, passwd_mi)
        exec_msg, success = runner.login_and_post_step(min_step, max_step)
        log_str += runner.log_str
        log_str += f'{exec_msg}\n'
        exec_result = {"user": user_mi, "success": success,
                       "msg": exec_msg}
    except:
        log_str += f"执行异常:{traceback.format_exc()}\n"
        log_str += traceback.format_exc()
        exec_result = {"user": user_mi, "success": False,
                       "msg": f"执行异常:{traceback.format_exc()}"}
    print(log_str)
    return exec_result


def execute():
    exec_results = []
    if len(users) == len(passwords):
        idx, total = 0, len(users)
        if use_concurrent:
            import concurrent.futures
            with concurrent.futures.ThreadPoolExecutor() as executor:
                exec_results = executor.map(lambda x: run_single_account(total, x[0], *x[1]), enumerate(zip(users, passwords)))
        else:
            for user_mi, passwd_mi in zip(users, passwords):
                exec_results.append(run_single_account(total, idx, user_mi, passwd_mi))
                idx += 1
                if idx < total:
                    # 每个账号之间间隔一定时间请求一次，避免接口请求过于频繁导致异常
                    time.sleep(sleep_seconds)

        success_count = 0
        push_results = []
        for result in exec_results:
            push_results.append(result)
            if result['success'] is True:
                success_count += 1
        
        # 生成详细的通知内容
        summary = f"执行账号总数{total}，成功：{success_count}，失败：{total - success_count}"
        
        # 构建详细通知内容，包含每个账号的执行情况
        detail_content = f"小米运动步数修改结果\n{'-'*30}\n{summary}\n{'-'*30}\n"
        for result in push_results:
            success = result['success']
            user = desensitize_user_name(result["user"])
            msg = result["msg"]
            status = "✓ 成功" if success else "✗ 失败"
            detail_content += f"{status} | {user} | {msg}\n"
        detail_content += f"{'-'*30}\n执行时间：{format_now()}"
        
        print(summary)
        
        # 使用青龙官方notify.py发送详细通知
        send_notification("小米运动步数修改结果", detail_content)
    else:
        print(f"账号数长度[{len(users)}]和密码数长度[{len(passwords)}]不匹配，跳过执行")
        exit(1)


# 发送通知函数
def send_notification(title, content):
    try:
        # 直接导入notify模块并调用send函数
        import notify
        notify.send(title, content)
        print("通知发送成功")
    except Exception as e:
        print(f"直接导入notify模块发送通知失败：{e}")
        # 尝试从不同路径导入
        import sys
        notify_paths = [
            ".",  # 当前目录
            "/ql",  # 青龙面板根目录
            "/ql/scripts",
            "..",
            "../..",
            "/usr/local/ql",
            "/root/ql"
        ]
        success = False
        for path in notify_paths:
            if path not in sys.path:
                sys.path.append(path)
            try:
                import notify
                notify.send(title, content)
                print(f"从路径 {path} 导入notify模块发送通知成功")
                success = True
                break
            except Exception as e2:
                # 清除已导入的notify模块，以便重新尝试
                if 'notify' in sys.modules:
                    del sys.modules['notify']
                continue
        if not success:
            print("所有路径导入notify模块均失败，跳过通知发送")


# 全局配置变量
config = {}

if __name__ == "__main__":
    # 北京时间
    time_bj = get_beijing_time()
    
    # 检查是否配置了环境变量
    if 'xmydb' not in os.environ:
        print("未配置xmydb变量，无法执行")
        exit(1)
    
    xmydb_value = os.environ.get('xmydb')
    users = []
    passwords = []
    
    try:
        # 尝试解析为JSON格式
        json_config = json.loads(xmydb_value)
        config.update(json_config)
        
        # 检查是否有ACCOUNTS字段（新完整格式）
        if 'ACCOUNTS' in json_config:
            accounts = json_config['ACCOUNTS'].split(';')
            if len(accounts) % 2 != 0:
                print("ACCOUNTS格式不正确，必须是手机号;密码;手机号;密码...")
                exit(1)
            for i in range(0, len(accounts), 2):
                users.append(accounts[i])
                passwords.append(accounts[i+1])
        else:
            # 兼容旧的USER/PWD格式
            users_str = json_config.get('USER', '')
            passwords_str = json_config.get('PWD', '')
            if users_str and passwords_str:
                users = users_str.split(';')
                passwords = passwords_str.split(';')
            else:
                # JSON格式但没有账号密码，退出
                print("JSON格式中未配置账号密码")
                exit(1)
    except json.JSONDecodeError:
        # 不是JSON格式，尝试解析为简化格式：手机号;密码;手机号;密码...
        accounts = xmydb_value.split(';')
        if len(accounts) % 2 != 0:
            print("xmydb格式不正确，简化格式必须是：手机号;密码;手机号;密码...")
            exit(1)
        for i in range(0, len(accounts), 2):
            users.append(accounts[i])
            passwords.append(accounts[i+1])
    except Exception as e:
        print(f"解析xmydb失败：{e}")
        exit(1)
    
    if not users or not passwords:
        print("未配置有效账号密码，无法执行")
        exit(1)
    
    if len(users) != len(passwords):
        print("账号和密码数量不匹配")
        exit(1)
    

    
    # 处理睡眠间隔
    sleep_seconds = float(config.get('SLEEP_GAP', '5'))
    
    # 获取步数范围
    min_step, max_step = get_min_max_by_time()
    
    # 处理并发配置
    use_concurrent = config.get('USE_CONCURRENT') == 'True'
    if not use_concurrent:
        print(f"多账号执行间隔：{sleep_seconds}")
    
    execute()
